    //wisata
    static ArrayList<Adapter> getListData() {
        ArrayList<Adapter> list = new ArrayList<>();
        for (int position = 0; position < wisataNames.length; position++) {
            Adapter adapter = new Adapter();
            adapter.setName(wisataNames[position]);
            adapter.setDetail(wisataDetails[position]);
            adapter.setKategori(wisataKategori[position]);
            adapter.setPhoto(wisataImages[position]);
            list.add(adapter);
        }
        return list;
    }

    //budaya
    static ArrayList<Adapter> getListBdy() {
        ArrayList<Adapter> listBdy = new ArrayList<>();
        for (int position = 0; position < budayaNama.length; position++) {
            Adapter adapterBdy = new Adapter();
            adapterBdy.setNamaBudaya(budayaNama[position]);
            adapterBdy.setDetailBudaya(budayaDetail[position]);
            adapterBdy.setImgBudaya(budayaImg[position]);
            listBdy.add(adapterBdy);
        }
        return listBdy;
    }

